package al.bruno;

import al.bruno.data.source.DictionaryRepository;
import al.bruno.di.DaggerDictionaryComponent;
import al.bruno.model.Dictionary;

import javax.inject.Inject;

public class App {
    private final String FORMAT = "%1$-10s %2$20s %3$35s %4$35s";
    private final String FORMAT_SUB_STRING = "%1$-10s %2$20s %3$35s %4$35s";
    public static void main(String[] args) {
        System.out.print("************************************\n");
        System.out.print("*      Dictionary Start Running    *\n");
        System.out.print("************************************\n");
        new App(args);
    }
    public App() {}
    private App(String[] args) {
        // DaggerDictionaryComponent.builder().application(this).build().inject(this);
        DaggerDictionaryComponent.factory().create(this).inject(this);
        dictionaryRepository.put(new Dictionary("test", "test"));
        System.out.println(String.format(FORMAT, "Nr", "TEXT", "COMMENT", "DATE"));
        dictionaryRepository.list().forEach(dictionary -> System.out.println(String.format(FORMAT,dictionary.getId(), dictionary.getText(), dictionary.getComment(), dictionary.getDate().toString())));
    }

    @Inject
    DictionaryRepository dictionaryRepository;
}
